	public static double toKWR(double dollar) {
		RATE = 112100.0/100;
		System.out.println((int)dollar+"달러는 "+RATE*dollar+"원 입니다.");
		return 0.;
	}

	public static void setRate(double r) {
		RATE = r;
	}

	public static void main(String[] args) {
		/* 구현합니다. */
		Scanner sc = new Scanner(System.in);
		
		while(true){
			System.out.println("1. 한국 원화를 달러로 변환");
			System.out.println("2. 달러를 한국 원화로 변환");
			System.out.println("3. 종료");
			System.out.print("번호를 선택하세요 >> ");
		
			int num = sc.nextInt();
		
			if(num == 1) {	// 한국 원화를 달러로 변환
				System.out.print("한국 원화를 입력 하세요 : ");
				int won = sc.nextInt();
				CurrencyConverter.toDollar(won);
			} else if(num == 2) {	// 달러를 한국 원화로 변환
				System.out.print("달러를 입력 하세요 : ");
				int dollar = sc.nextInt();
				CurrencyConverter.toKWR(dollar);
			} else if(num == 3) {	// 프로그램 종료
				return;
			} else {	// 잘못 입력했을경우
				System.out.println("잘못 입력하였습니다. 다시 입력해주세요.");
				continue;
			}
		}
		
	}
